import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Registration = () => {
  const [users, setUsers] = useState([]); // Initialize as an empty array

  useEffect(() => {
    fetch("http://localhost:8000/students")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => setUsers(data)) // Update to use setUsers for setting the array
      .catch((err) => console.log("Fetch error: ", err.message));
  }, []);

  return (
    <div className="container">
      <h2>Registration Application</h2>
      <div className="table-container">
        <Link to="/user/create" className="btn btn-add">
          Add new Student
        </Link>

        {/* Conditionally render the table or a message if no users */}
        {users.length === 0 ? (
          <p>
            No users found. <Link to="/user/create">Add a User</Link>
          </p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Date of Birth</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.dateOfBirth}</td>{" "}
                  {/* Assuming the field is 'dateOfBirth' */}
                  <td>
                    <a href="#" className="btn btn-info">
                      View
                    </a>
                    <a href="#" className="btn btn-primary">
                      Edit
                    </a>
                    <a href="#" className="btn btn-danger">
                      Delete
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Registration;
